import sys

def fibonacci(n):
    """Generates the Fibonacci sequence up to the nth term."""
    if n <= 0:
        return []

    a, b = 0, 1
    result = [a]

    if n == 1:
        return result

    # Generate n terms. We already have 1 term (a=0).
    for _ in range(n - 1):
        result.append(b)
        a, b = b, a + b

    return result

if __name__ == "__main__":
    try:
        # Get N from command line arguments, default to 10
        if len(sys.argv) > 1:
            n_terms = int(sys.argv[1])
        else:
            n_terms = 10
            print(f"Using default number of terms: {n_terms}")

        if n_terms < 0:
            print("Please provide a non-negative integer.")
        else:
            sequence = fibonacci(n_terms)
            print(f"Fibonacci sequence (first {n_terms} terms):\n{sequence}")
    except ValueError:
        print("Error: Input must be a valid integer.")
    except Exception as e:
        print(f"An error occurred: {e}")

